import axios from "axios";
import { useState } from "react";
import { useCookies } from "react-cookie";
import { useNavigate } from "react-router-dom";

const Form = ({
  username,
  setUsername,
  password,
  setPassword,
  title,
  onSubmit,
}) => {
  return (
    <div className="flex flex-col justify-center items-center p-5 bg-white rounded-md shadow-md m-5 w-96">
      <form onSubmit={onSubmit}>
        <h2 className="mt-0 text-2xl font-bold mb-4">{title}</h2>
        <div className="flex flex-col mb-5">
          <label htmlFor="username" className="font-bold mb-1">
            Username:
          </label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
            className="p-2 text-base rounded-md border border-gray-300"
          />
        </div>
        <div className="flex flex-col mb-5">
          <label htmlFor="password" className="font-bold mb-1">
            Password:
          </label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
            className="p-2 text-base rounded-md border border-gray-300"
          />
        </div>
        <button
          className="p-3 px-8 rounded-lg text-sm uppercase font-medium tracking-wider mt-3 inline-block shadow-md bg-black text-white"
          type="submit"
        >
          {title}
        </button>
      </form>
    </div>
  );
};

export const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const onSubmit = async (event) => {
    // refresh the page when we click on submit
    event.preventDefault();

    // we use AXIOS to post request for the API (fetching library)
    try {
      await axios.post("http://localhost:3001/auth/register", {
        username,
        password,
      });
      alert("Registration Completed! Now login :) ");
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <Form
      username={username}
      setUsername={setUsername}
      password={password}
      setPassword={setPassword}
      title="Register"
      onSubmit={onSubmit}
    />
  );
};

export const Login = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const [_, setCookies] = useCookies(["acces_token"])
  const navigate = useNavigate()

  const onSubmit = async(event) => {
    event.preventDefault()

    try{
        const response = await axios.post("http://localhost:3001/auth/login", {
            username,
            password,
          });

          setCookies("acces_token", response.data.token);
          window.localStorage.setItem("userID", response.data.userID);
          navigate("/")
    } catch(err) {
        console.error(err);
    }

  }; 

  return (
    <Form
      username={username}
      setUsername={setUsername}
      password={password}
      setPassword={setPassword}
      title="Login"
      onSubmit={onSubmit}
    />
  );
};

export default function Auth() {
  return (
    <div className="flex justify-center items-center h-screen bg-gray-100">
      <Login />
      <Register />
    </div>
  );
}
