import { useState } from "react";

export default function Auth() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  const Form = ({ username, setUsername, password, setPassword }) => {
    return (
      <div class="flex flex-col justify-center items-center p-5 bg-white rounded-md shadow-md m-5 w-96">
        <form>
          <h2 class="mt-0">Register</h2>
          <div class="flex flex-col mb-5">
            <label htmlFor="username" class="font-bold mb-1">
              Username:{" "}
            </label>
            <input
              type="text"
              id="username"
              value={username}
              onChange={(event) => setUsername(event.target.value)}
              class="p-2 text-base rounded-md border border-gray-300"
            />
          </div>
          <div class="flex flex-col mb-5">
            <label htmlFor="password" class="font-bold mb-1">
              Password:{" "}
            </label>
            <input
              type="text"
              id="password"
              value={password}
              onChange={(event) => setPassword(event.target.value)}
              class="p-2 text-base rounded-md border border-gray-300"
            />
          </div>

          <button
            className="p-3 px-8 rounded-lg text-sm uppercase font-medium tracking-wider mt-3 inline-block shadow-md bg-black text-white"
            type="submit"
          >
            Register
          </button>
        </form>
      </div>
    );
  };

  const Register = () => {
    return (
      <Form
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
      />
    );
  };

  const Login = () => {
    return (
      <Form
        username={username}
        setUsername={setUsername}
        password={password}
        setPassword={setPassword}
      />
    );
  };

  return (
    <div class="flex justify-center items-center h-screen bg-gray-100">
      <Login />
      <Register />
    </div>
  );
}
