import { createContext, useState } from "react";
import { useNavigate } from "react-router-dom";

export const GlobalContext = createContext(null);  //initial value 

export default function GlobalState({children}){

        const [searchParam, setSearchParam] = useState('');
        const [loading, setLoading] = useState(false);
        const [recipeList, setRecipeList] = useState([]);
        const [recipeDetailsData,setRecipeDetailsData] = useState(null);
        const [planningList, setPlanningList] = useState([])

        const navigate = useNavigate()
        
        async function handleSubmit(event) {
            event.preventDefault()
            try {
                const res = await fetch(`https://forkify-api.herokuapp.com/api/v2/recipes?search=${searchParam}`);

                const data = await res.json(); 
                if(data?.data?.recipes){
                    setRecipeList(data?.data?.recipes);
                    setLoading(false);
                    setSearchParam(''); 
                    navigate('/') 
                }
            } catch(e){
                console.log(e)
                setLoading(false)
                setSearchParam('')
            }            
        }

        function handleAddToPlanning(getCurrentItem){
            console.log(getCurrentItem);
            let copyPlanningList = [...planningList]
            const index = copyPlanningList.findIndex(item => item.id === getCurrentItem.id)

            if(index === -1){
                copyPlanningList.push(getCurrentItem)
            } else {copyPlanningList.splice(index)}

            setPlanningList(copyPlanningList)
        };
        

    return <GlobalContext.Provider value={{searchParam,
                                            loading,
                                            recipeList,
                                            recipeDetailsData,
                                            planningList,
                                            setSearchParam, 
                                            handleSubmit,
                                            setRecipeDetailsData,
                                            handleAddToPlanning}}>{children}</GlobalContext.Provider>
}
