import { createContext, useState } from "react";

export const GlobalContext = createContext(null);  //initial value 

export default function GlobalState({children}){

        const [searchParam, setSearchParam] = useState('');
        const [loading, setLoading] = useState(false);
        const [recipeList, setRecipeList] = useState([]);
        const [recipeDetailsData,setRecipeDetailsData] = useState(null);
        const [favoriteList, setFavoritesList] = useState([])

        async function handleSubmit(event) {
            event.preventDefault()
            try {
                const res = await fetch(`https://forkify-api.herokuapp.com/api/v2/recipes?search=${searchParam}`);

                const data = await res.json(); 
                if(data?.data?.recipes){
                    setRecipeList(data?.data?.recipes)
                    setLoading(false)
                    setSearchParam('')
                }
            } catch(e){
                console.log(e)
                setLoading(false)
                setSearchParam('')
            }

            function handleAddToFavorite(getCurrentItem){
                console.log(getCurrentItem)
            };
            console.log(loading, recipeList)
            
        }

    return <GlobalContext.Provider value={{searchParam,
                                            loading,
                                            recipeList,
                                            recipeDetailsData,
                                            handleAddToFavorite,
                                            setSearchParam, 
                                            handleSubmit,
                                            setRecipeDetailsData}}>{children}</GlobalContext.Provider>
}
