import { useState } from "react";


export default function CreateRecipes() {

    const [recipe, setRecipe] = useState({
        title:"",
        ingredients: [],
        instructions: "",
        imageURL:"",
        type:"",
        userOwner:0,
    })

    const handleChange = (event) => {
        const {name, value} = event.target.value 
        setRecipe({...recipe, [name]: value})
    };

    const handleIngredientChange = (event) => {
        const {value} = event.target.value 
        const ingredients = recipe.ingredients;
        ingredients[index] = value;
        setRecipe({...recipe, ingredients: ingredients})
        console.log(recipe)

    };

    const addIngredient = () =>{
        setRecipe({...recipe, ingredients: [...recipe.ingredients, ""]})
    }

    return(
        <div  class="flex flex-col justify-center items-center p-5 bg-white rounded-md shadow-md m-5 w-[400px]">
            <h2 class="mt-0">Create Recipe</h2>
            <form class="flex flex-col">
                <label htmlFor="title" class="font-bold mb-[5px]">Title</label>
                <input type="text" id="title" name="title" onChange={handleChange} class="p-2 text-base rounded-md border border-gray-500"/>

                <label htmlFor="type" class="font-bold mb-[5px]">Type</label>
                <input type="text" id="type" name="type" onChange={handleChange} class="p-2 text-base rounded-md border border-gray-500"/>

                <label htmlFor="ingredients" class="font-bold mb-[5px]">Ingredients</label>
                {
                    recipe.ingredients.map((ingredient, index)=>(
                        <input key={index} type="text" name="ingredients" value={ingredient} onChange={(event)=>handleIngredientChange(event, index)}></input>
                    ))
                }
                <button onClick={addIngredient}>Add Ingredient</button>
                <input type="text" id="ingredients" name="ingredients" onChange={handleChange} class="p-2 text-base rounded-md border border-gray-500"/>

                <label htmlFor="instructions" class="font-bold mb-[5px]">Instructions</label>
                <textarea name="instructions" id="instructions" onChange={handleChange} class="p-2 text-base rounded-md border border-gray-500"></textarea>

                <label htmlFor="imageURL" class="font-bold mb-[5px]">Image URL</label>
                <input type="text" id="imageURL" name="imageURL" onChange={handleChange} class="p-2 text-base rounded-md border border-gray-500" />
            </form>
        </div>
    )
}
