import { useState } from "react";



export const Register = () => {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");

  return (
    <Form
      username={username}
      setUsername={setUsername}
      password={password}
      setPassword={setPassword}
      title="Register"
    />
  );
};

export const Login = () => {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");

return (
    <Form
    username={username}
    setUsername={setUsername}
    password={password}
    setPassword={setPassword}
    title="Login"
    />
);
};
  
const Form = ({ username, setUsername, password, setPassword, title }) => {
  return (
    <div className="flex flex-col justify-center items-center p-5 bg-white rounded-md shadow-md m-5 w-96">
      <form>
        <h2 className="mt-0 text-2xl font-bold mb-4">{title}</h2>
        <div className="flex flex-col mb-5">
          <label htmlFor="username" className="font-bold mb-1">
            Username:
          </label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
            className="p-2 text-base rounded-md border border-gray-300"
          />
        </div>
        <div className="flex flex-col mb-5">
          <label htmlFor="password" className="font-bold mb-1">
            Password:
          </label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
            className="p-2 text-base rounded-md border border-gray-300"
          />
        </div>
        <button
          className="p-3 px-8 rounded-lg text-sm uppercase font-medium tracking-wider mt-3 inline-block shadow-md bg-black text-white"
          type="submit"
        >
          {title}
        </button>
      </form>
    </div>
  );
};

export default function Auth(){
    return (
      <div className="flex justify-center items-center h-screen bg-gray-100">
        <Login />
        <Register />
      </div>
    );
  };
