import { useState } from "react";

export default function CreateRecipes() {
  const [recipe, setRecipe] = useState({
    title: "",
    ingredients: [],
    instructions: "",
    imageURL: "",
    type: "",
    userOwner: 0,
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setRecipe({ ...recipe, [name]: value });
  };

  const handleIngredientChange = (event, index) => {
    const { value } = event.target;
    const ingredients = [...recipe.ingredients];
    ingredients[index] = value;
    setRecipe({ ...recipe, ingredients });
  };

  const handleAddIngredient = () => {
    const ingredients = [...recipe.ingredients, ""];
    setRecipe({ ...recipe, ingredients });
  };

  return (
    <div class="flex flex-col justify-center items-center p-5 bg-white rounded-md shadow-md m-5 w-[400px]">
      <h2 class="mt-0">Create Recipe</h2>
      <form class="flex flex-col">

        <label htmlFor="title" class="font-bold mb-[5px]">
          Title
        </label>
        <input
          type="text"
          id="title"
          name="title"
          onChange={handleChange}
          class="p-2 text-base rounded-md border border-gray-500"
        />

        <label htmlFor="type" class="font-bold mb-[5px]">
          Type
        </label>
        <input
          type="text"
          id="type"
          name="type"
          onChange={handleChange}
          class="p-2 text-base rounded-md border border-gray-500"
        />

        <label htmlFor="ingredients" class="font-bold mb-[5px]">
          Ingredients
        </label>
        {recipe.ingredients.map((ingredient, index) => (
          <input
            key={index}
            type="text"
            name="ingredients"
            value={ingredient}
            onChange={(event) => handleIngredientChange(event, index)}
          />
        ))}
        <button class="p-2 text-base rounded-md border border-gray-500  bg-[#9c9c9c] text-black" onClick={handleAddIngredient}>Add Ingredient</button>

        <label htmlFor="instructions" class="font-bold mb-[5px]">
          Instructions
        </label>
        <textarea
          name="instructions"
          id="instructions"
          onChange={handleChange}
          class="p-2 text-base rounded-md border border-gray-500"
        ></textarea>

        <label htmlFor="imageURL" class="font-bold mb-[5px]">
          Image URL
        </label>
        <input
          type="text"
          id="imageURL"
          name="imageURL"
          onChange={handleChange}
          class="p-2 text-base rounded-md border border-gray-500"
        />
      </form>
    </div>
  );
}
